mapper.select:
select count(1)
from ERROR_SPEC A
WHERE 1 = 1

    AND A.FLOW_ID = #{flowId,jdbcType=VARCHAR}


    AND A.MACHIN_CODE = #{machinCode,jdbcType=VARCHAR}


    AND A.ERROR_CODE = #{errorCode,jdbcType=VARCHAR}


    AND A.ERROR_TITLE = #{errorTitle,jdbcType=VARCHAR}

SELECT A.ERROR_SPEC_ID, A.ERROR_CODE, A.ERROR_TITLE, A.ERROR_SPEC, A.DEAL_METHOD, A.MACHIN_CODE, A.STATE, DECODE(A.STATE,'1','生效','失效') STATE_NAME, TO_CHAR(A.CREATE_DATE,'YYYY-MM-DD') CREATE_DATE, A.FLOW_ID, ROWNUM rownumber FROM ERROR_SPEC A WHERE 1 = 1
AND A.FLOW_ID = #{flowId,jdbcType=VARCHAR}
AND A.MACHIN_CODE = #{machinCode,jdbcType=VARCHAR}
AND A.ERROR_CODE = #{errorCode,jdbcType=VARCHAR}
AND A.ERROR_TITLE = #{errorTitle,jdbcType=VARCHAR}

SELECT A.ERROR_SPEC_ID, A.ERROR_CODE, A.ERROR_TITLE, A.ERROR_SPEC, A.DEAL_METHOD, A.MACHIN_CODE, A.STATE, DECODE(A.STATE,'1','生效','失效') STATE_NAME, TO_CHAR(A.CREATE_DATE,'YYYY-MM-DD') CREATE_DATE, A.FLOW_ID, a.channel_no FROM ERROR_SPEC A WHERE 1 = 1
AND A.FLOW_ID = #{flowId,jdbcType=VARCHAR}
AND A.MACHIN_CODE = #{machinCode,jdbcType=VARCHAR}
AND A.ERROR_CODE = #{errorCode,jdbcType=VARCHAR}
AND A.ERROR_TITLE = #{errorTitle,jdbcType=VARCHAR}

SELECT A.ERROR_SPEC_ID,
A.ERROR_CODE,
A.ERROR_TITLE,
A.ERROR_SPEC,
A.DEAL_METHOD,
A.MACHIN_CODE,
A.STATE,
DECODE(A.STATE,'1','生效','失效') STATE_NAME,
TO_CHAR(A.CREATE_DATE,'YYYY-MM-DD') CREATE_DATE,
A.FLOW_ID
FROM ERROR_SPEC A
WHERE 1 = 1

    AND A.ERROR_SPEC_ID = #{errorSpecId,jdbcType=VARCHAR}


    AND A.FLOW_ID = #{flowId,jdbcType=VARCHAR}


    AND A.MACHIN_CODE = #{machinCode,jdbcType=VARCHAR}


    AND A.ERROR_CODE = #{errorCode,jdbcType=VARCHAR}


    AND A.ERROR_TITLE = #{errorTitle,jdbcType=VARCHAR}


    AND A.STATE = #{state,jdbcType=VARCHAR}
